import styled from "styled-components";

interface Props {
  numeroDoConcurso: number;
  dataPorExtenso: string;
}

export default function NomeLoteria({ quantidadeGanhadores }: Props) {
  return (
    <Sld>
      {quantidadeGanhadores === 0
        ? "ACUMULOU!"
        : `${quantidadeGanhadores} GANHADORES`}
    </Sld>
  );
}

const Sld = styled.div`
  margin-top: 15px;
  color: ${(props) => props.theme.data};
  padding-left: 15px;
  font-size: 15px;
`;

<div className="mega-data-concurso">
  {`Concurso ${sorteio.numeroDoConcurso} - ${sorteio.dataPorExtenso}`}
</div>;
